<node CREATED="1423125500614" MODIFIED="1423125500614" TEXT="Fourth component of p = p.w = p.a = p.v = p[3]"/>
<node CREATED="1423125643287" MODIFIED="1423125643287" TEXT="vec4 p = vec4(1, 2, 3, 4);"/>
