<node CREATED="1465264743184" ID="ID_162675457" MODIFIED="1465264754766" TEXT="Parity(XOR)"/>
<node CREATED="1465264755285" ID="ID_1419976035" MODIFIED="1465264811784" TEXT="Addition/Multiplication in Galois field"/>
<node CREATED="1465264816531" ID="ID_841364259" MODIFIED="1465264827358" TEXT="Reed-Solomon error correction"/>
</node>
<node CREATED="1465264860539" ID="ID_660607827" MODIFIED="1465264861175" TEXT="hybrid RAID">
<node CREATED="1465264873836" ID="ID_644537995" MODIFIED="1465264874488" TEXT="a fast SSD can be mirrored with a mechanical drive"/>
</node>
<node CREATED="1465264943806" ID="ID_1675860127" MODIFIED="1465264944334" TEXT="RAID levels and their associated data formats are standardized by the Storage Networking Industry Association (SNIA) in the Common RAID Disk Drive Format (DDF) standard"/>
</node>
</node>
